CREATE TABLE credentials(    id SERIAL NOT NULL,    username text NOT NULL,    password text NOT NULL,    PRIMARY KEY (id));CREATE TABLE users(    id SERIAL NOT NULL,    username text NOT NULL,    email text NOT NULL,    name text NOT NULL,    user_profile text NOT NULL,    PRIMARY KEY (id));CREATE TABLE event_archive_map(    event_number integer,    username text NOT NULL);CREATE TABLE event_shared_map(    username text NOT NULL,    shared_by text NOT NULL,    id integer NOT NULL GENERATED ALWAYS AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),    event_no integer NOT NULL,    CONSTRAINT event_shared_map_pkey PRIMARY KEY (id));CREATE TABLE event_user_map(    id integer NOT NULL GENERATED ALWAYS AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),    event_no integer NOT NULL,    username text NOT NULL,    CONSTRAINT "EVENT_USER_MAP_pkey" PRIMARY KEY (id));CREATE TABLE events(    event_name text NOT NULL,    description text,    date date NOT NULL,    location text NOT NULL,    ticket_price double precision NOT NULL,    host text NOT NULL,    capacity integer NOT NULL,    id integer NOT NULL GENERATED ALWAYS AS IDENTITY ( INCREMENT 1 START 1 MINVALUE 1 MAXVALUE 2147483647 CACHE 1 ),    CONSTRAINT event_unique UNIQUE (event_name));